def find_substring(s, words)
  s_l = s.size
  w_l = words.first.size
  w_s = words.size
  a = s.split('')
  [].tap do |r|
    dict = {}.tap { |d| words.each { |w| d[w] = d[w] ? (d[w]+1) : 1 } }
    (0...w_l).each do |i|
      count, left = 0, i
      cur_dict = {}
      (i..(s_l - w_l)).step(w_l).each do |j|
        sub = a[j, w_l].join ''
        if dict[sub]
          cur_dict[sub] = cur_dict[sub] ? (cur_dict[sub]+1) : 1
          if cur_dict[sub] <= dict[sub]
            count+=1
          else
            while left < s_l-w_l
              tmp = a[left, w_l].join ''
              cur_dict[tmp] -= 1
              left += w_l
              if sub == tmp
                break
              else
                count -= 1
              end
            end
          end
          if count == w_s
            r.push left
            tmp = a[left, w_l].join ''
            cur_dict[tmp] -= 1
            left += w_l
            count -= 1
          end
        else
          count = 0
          cur_dict = {}
          left = j + w_l
        end
      end
    end
  end
end

